//   product: {},
// };

export const fetchProducts = createAsyncThunk(
  "products/fetchAll",
  (dispatch) => {
    axios
      .get("https://fakestoreapi.com/products")
      .then((response) => response.data)
      .catch((err) => console.log(err));
  }
);

export const productSlice = createSlice({
  name: "products",
  initialState: {
    products: [],
    product: {},
  },
  reducers: {
    setProducts,
  },
  extraReducers: {},
});

